{{JSON.stringify({
	variables:{
	"input": [
    {
      "code": Date(),
      "operationsType": "Production",
      "quantity": parseFloat(Quantity.text),
      "quantityUoM": {
        "code": Table1.selectedRow.Material_Base_UoM
      },
      "requestState": {
        "name": "Unscheduled"
      },
      "operationsDefinition": {
        "id": Select1.selectedOptionValue
      },
			"equipment": {
			 "id": SelectEquipment.selectedOptionValue
			}
    }
  ]
},
	query:`
		mutation addOperationsRequest($input:[AddOperationsRequestInput!]!){
  addOperationsRequest(input:$input){
    operationsRequest{
      id
    }
  }
}
	`
})}}

