actionConfiguration.body:
{{JSON.stringify({
	variables:{"input":SelectEquipment.selectedOptionValue},
	query:`
	query QueryEquipment($input:ID!){  
  getEquipment (id:$input)@cascade{
    id
    materialEquipment{
				material{
        code
        name
          baseUnitOfMeasure{
            code
          }
      }
      planningSummaryByState(filter:{isPlanned:true}){
        state{
          status{
            code
          }
					isPlanned
        }
        inventoryMax
        inventoryMin
        productionOrderQuantity
        stockOnHand
        saleOrderQty
      }
    }
  }
}
	`
})}}

